import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import posed from 'react-pose';
import { transform } from "popmotion";
import ReactDOM from "react-dom";
import shuffle from './shuffle';

const { pipe, clamp, interpolate, blendColor } = transform;

const Box = posed.div({
  hidden: { opacity: 0 },
  visible: { opacity: 1 }
});

const Circle = posed.div({
  pressable: true,
  hoverable: true,
  init: { scale: 1 },
  press: { scale: 0.6 },
  hover: { scale: 1.2 },
  hidden: { opacity: 0 , transition: { duration: 1000 } },
  visible: { opacity: 0.8, transition: { duration: 2000 } }
});

const Item = posed.li({
  // You can make a custom transition for the flip-powered
  // shuffling animation by overriding `flip`. You can even
  // add other properties to animate while the flip animation
  // is in progress. Uncomment the following code to try it out!
  // flip: {
  //   scale: 1,
  //   transition: {
  //     scale: {
  //       type: 'spring',
  //       velocity: 10
  //     },
  //     default: {
  //       type: 'spring'
  //     }
  //   }
  // }
});

class App extends Component {

  constructor(props, context) {
    super(props, context)
    this.state = { 
      isVisible: true, 
      isVisibleCircle: false,
      items: [0, 1, 2, 3, 4, 5, 6, 7]
    };
    this.pressEnd = this.pressEnd.bind(this)
  }

  componentDidMount() {
    setInterval(() => {
      this.setState({ isVisible: !this.state.isVisible });
    }, 500);

    setInterval(() => {
      this.setState({
        items: shuffle(this.state.items)
      });
    }, 1000);

    setTimeout(()=> {
      const node = ReactDOM.findDOMNode(this);
      // Get child nodes
      if (node instanceof HTMLElement) {
        const child = node.querySelector('.someClass');
      }
      node.querySelector('.boxesContainer').style.display = "none"
      this.state.isVisibleCircle = true
    }, 300)
  }

  pressEnd(){
    this.setState({isVisibleCircle:false});
  }

  render() {
    const { isVisible } = this.state;
    const {isVisibleCircle} = this.state;
    const { items } = this.state;

    return (
      <div className="App">
        <div className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
        </div>
        <div className="boxesContainer">
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
        </div>
        <Circle className="circle" onPressEnd={this.pressEnd} pose={isVisibleCircle ? 'visible' : 'hidden'} />

      </div>
    );
  }
}

export default App;
