import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import posed from 'react-pose';
import { transform } from "popmotion";
import ReactDOM from "react-dom"

const { pipe, clamp, interpolate, blendColor } = transform;

const Box = posed.div({
  hidden: { opacity: 0 },
  visible: { opacity: 1 }
});

const Circle = posed.div({
  pressable: true,
  init: { scale: 1 },
  press: { scale: 0.6 },
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { duration: 2000 } }
});


class App extends Component {

  state = { isVisible: true };
  stateCircle = {isVisibleCircle: false};

  componentDidMount() {
    setInterval(() => {
      this.setState({ isVisible: !this.state.isVisible });
    }, 500);

    setTimeout(()=> {
      const node = ReactDOM.findDOMNode(this);
      // Get child nodes
      if (node instanceof HTMLElement) {
        const child = node.querySelector('.someClass');
      }
      node.querySelector('.boxesContainer').style.display = "none"
      this.stateCircle.isVisibleCircle = true
    }, 3000)
  }

  render() {
    const { isVisible } = this.state;
    const {isVisibleCircle} = this.stateCircle
    return (
      <div className="App">
        <div className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
        </div>
        <div className="boxesContainer">
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
        </div>
        <Circle className="circle" pose={isVisibleCircle ? 'visible' : 'hidden'} />

      </div>
    );
  }
}

export default App;
