import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import posed from 'react-pose';

const Box = posed.div({
  hidden: { opacity: 0 },
  visible: { opacity: 1 }
});

const { decay, transform } = popmotion
const { pipe, clamp, interpolate, blendColor } = transform
const Circle = posed.div({
  visible: {
    opacity: 0.5,
    scaleY: 1,
    transition: {
      type: 'physics',
      delay: 400
    }
  },
  draggable:true,
  init: { scale: 1 },
  drag: { scale: 1.2 },
  dragEnd: {
    x: 0,
    y: 0,
    transition: { type: 'spring' }
  },
  passive: {
    backgroundColor: ['x', pipe(
    interpolate([-200, 200], [0, 1]),
    clamp(0, 1),
    blendColor('#FF1C68', '#198FE3')
  )]
}
});

class App extends Component {

  state = { isVisible: true };

  componentDidMount() {
    setInterval(() => {
      this.setState({ isVisible: !this.state.isVisible });
    }, 500);
  }

  render() {
    const { isVisible } = this.state;
    return (
      <div className="App">
        <div className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </div>
        <div className="boxesContainer">
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
          <Box className="box" pose={isVisible ? 'visible' : 'hidden'} />
        </div>
        <Circle className="circle" pose={isVisible ? 'visible' : 'hidden'} />

      </div>
    );
  }
}

export default App;
